export default {
  data: function data() {
    return {
      dates: ['2019-09-10', '2019-09-20'],
      reportDisplay: false,
      totalIncome: 0
    };
  },
  methods: {
    actionGenerate: function actionGenerate() {
      this.reportDisplay = true;
      var start = this.dates[0];
      var end = this.dates[1];
      var range = {
        startDate: start,
        endDate: end
      };
      this.startDay = start;
      this.endDay = end;
      var orderObj = [];
      var otherEntryObj = [];
      var days = 0;
      days = (this.dateRange[1] - this.dateRange[0]) / (1000 * 3600 * 24) + 1;
      this.$http.get("/ordapi/getOrderStatement", {
        params: range
      }).then(function (response) {
        var _this = this;

        orderObj = response.data;
        this.$http.get("/othentryapi/getOtherEntryRange", {
          params: range
        }).then(function (response) {
          otherEntryObj = response.data;

          _this.calculateAndAnalysis(orderObj, otherEntryObj, days);
        });
      });
    },
    calculateAndAnalysis: function calculateAndAnalysis(orderObj, otherEntryObj, days) {
      //alert(orderObj.length);
      //alert(otherEntryObj.length);
      var _totalIncome = 0,
          _totalProfit = 0,
          _averageIncome = 0,
          _averageProfit = 0;
      var _incomeMianjian = 0,
          _incomeXiyao = 0,
          _incomeYaowan = 0,
          _incomeCaoyao = 0;
      var _profitMianjian = 0,
          _profitXiyao = 0,
          _profitYaowan = 0,
          _profitCaoyao = 0;
      var _taotalPatient = 0,
          _averagePatient = 0,
          _gt20Patient = 0,
          _lt20Patient = 0;
      var _jianyaoTimes = 0;
      var _iteratorNormalCompletion = true;
      var _didIteratorError = false;
      var _iteratorError = undefined;

      try {
        for (var _iterator = orderObj[Symbol.iterator](), _step; !(_iteratorNormalCompletion = (_step = _iterator.next()).done); _iteratorNormalCompletion = true) {
          var item = _step.value;

          if (item.medType == "免煎药") {
            _incomeMianjian = parseFloat((_incomeMianjian + item.total).toFixed(2));
            _profitMianjian = parseFloat((_profitMianjian + item.totalprofit).toFixed(2));
          } else if (item.medType == "草药") {
            _incomeCaoyao = parseFloat((_incomeCaoyao + item.total).toFixed(2));
            _profitCaoyao = parseFloat((_profitCaoyao + item.totalprofit).toFixed(2));
          } else if (item.medType == "西药") {
            _incomeXiyao = parseFloat((_incomeXiyao + item.total).toFixed(2));
            _profitXiyao = parseFloat((_profitXiyao + item.totalprofit).toFixed(2));
          }

          _totalIncome = parseFloat((_totalIncome + item.total).toFixed(2));
          _totalProfit = parseFloat((_totalProfit + item.totalprofit).toFixed(2));
          _taotalPatient = _taotalPatient + 1;

          if (parseInt(item.age) > 20) {
            _gt20Patient = _gt20Patient + 1;
          } else {
            _lt20Patient = _lt20Patient + 1;
          }

          var searchStr = JSON.stringify(item.med);

          if (searchStr.indexOf('煎药') != -1) {
            _jianyaoTimes = _jianyaoTimes + 1;
          }
        }
      } catch (err) {
        _didIteratorError = true;
        _iteratorError = err;
      } finally {
        try {
          if (!_iteratorNormalCompletion && _iterator.return != null) {
            _iterator.return();
          }
        } finally {
          if (_didIteratorError) {
            throw _iteratorError;
          }
        }
      }

      var _iteratorNormalCompletion2 = true;
      var _didIteratorError2 = false;
      var _iteratorError2 = undefined;

      try {
        for (var _iterator2 = otherEntryObj[Symbol.iterator](), _step2; !(_iteratorNormalCompletion2 = (_step2 = _iterator2.next()).done); _iteratorNormalCompletion2 = true) {
          var _item = _step2.value;

          if (_item.detailType == "药丸") {
            _incomeYaowan = parseFloat((_incomeYaowan + _item.amount).toFixed(2));
            _profitYaowan = parseFloat((_profitYaowan + _item.profit).toFixed(2));
          }

          _totalIncome = parseFloat((_totalIncome + _item.amount).toFixed(2));
          _totalProfit = parseFloat((_totalProfit + _item.profit).toFixed(2));
          _taotalPatient = _taotalPatient + 1;
        }
      } catch (err) {
        _didIteratorError2 = true;
        _iteratorError2 = err;
      } finally {
        try {
          if (!_iteratorNormalCompletion2 && _iterator2.return != null) {
            _iterator2.return();
          }
        } finally {
          if (_didIteratorError2) {
            throw _iteratorError2;
          }
        }
      }

      _averageIncome = parseFloat((_totalIncome / days).toFixed(2));
      _averageProfit = parseFloat((_totalProfit / days).toFixed(2));
      _averagePatient = parseFloat((_taotalPatient / days).toFixed(2)); //赋值

      this.totalIncome = _totalIncome;
      this.totalProfit = _totalProfit;
      this.averageIncome = _averageIncome;
      this.averageProfit = _averageProfit; //收入

      this.incomeMianjian = _incomeMianjian;
      this.incomeCaoyao = _incomeCaoyao;
      this.incomeXiyao = _incomeXiyao;
      this.incomeYaowan = _incomeYaowan; //利润

      this.profitMianjian = _profitMianjian;
      this.profitCaoyao = _profitCaoyao;
      this.profitXiyao = _profitXiyao;
      this.profitYaowan = _profitYaowan; //病人

      this.totalPatient = _taotalPatient;
      this.averagePatient = _averagePatient;
      this.gt20Patient = _gt20Patient;
      this.lt20Patient = _lt20Patient; //其它

      this.jianyaoTimes = _jianyaoTimes;
    }
  },
  mounted: function mounted() {
    this.getAll();
  },
  computed: {
    dateRangeText: function dateRangeText() {
      return this.dates.join(' ~ ');
    }
  }
