  export default {
    data: () => ({
      searchStr: '',
      medRadio: '草药',
      medTypeItems: ['草药','免煎','西药'],
      adddialog: false,
      loading: false,
      cardColor: 'green',
      dialogTitle: '',
      snackbar: false,
      snackbarColor: '',
      notification: '',
      headers: [
        {
          sortable: false,
          text: '药品名称',
          value: 'medname'
        },
        {
          sortable: false,
          text: '别名',
          value: 'alias'
        },
        {
          sortable: false,
          text: '规格',
          value: 'spec'
        },
        {
          sortable: false,
          text: '袋/盒',
          value: 'bagperbox',
        },
        {
          sortable: true,
          text: '数量',
          value: 'inventoryNm',
        },
        {
          sortable: false,
          text: '进价',
          value: 'baseprice',
        },
        {
          sortable: false,
          text: '零售价',
          value: 'sellprice',
        },
        {
          sortable: false,
          text: '监测',
          value: 'checked',
        },
        {
          sortable: false,
          text: '操作',
          value: 'action',
        },
      ],
      items: [],
      dialogMedName: "",
      dialogAlias: "",
      dialogSpec: "",
      dialogMedRadio: "草药",
      dialogBagPerBox: "",
      dialogInventoryNm: "",
      dialogBaseprice: "",
      dialogSellprice: "",
      dialogChecked: 0,
      dialogMedId: ""
    }),

    methods: {
      //搜索
      filterText (value, search, item) {
        return value != null &&
          search != null &&
          typeof value === 'string' &&
          value.toString().indexOf(search) !== -1
      },

      // 根据类型获取药品数据
      getAll: function() {
        this.loading = true;
        this.$http.get('/api/getAllMedbyType',{
          params: {
            dbs : this.$store.state.user.dbs_prefix+'medlist',
						medtype : this.medRadio
					}
        }).then( (res) => {
          this.items = res.data;
          this.loading = false;
        })
      },

      //当类型变化时改变颜色和重新读取数据
      radioChanged: function(){
        this.getAll();
        if(this.medRadio == "西药"){
          this.cardColor = 'blue lighten-2';
				}else if(this.medRadio == "免煎"){
          this.cardColor = 'lime darken-2';
				}else{
          this.cardColor = 'green';
        }					
      },

      backTop() {
        document.body.scrollTop = 0
        document.documentElement.scrollTop = 0
      },

      addMedShow: function(){
        this.adddialog = true;
        this.dialogTitle = "新增药品";
        this.dialogMedRadio = this.medRadio;
      },

      editItem: function(item){
        this.adddialog = true;
        this.dialogTitle = "修改药品";
        this.dialogMedName = item.medname;
        this.dialogAlias = item.alias;
        this.dialogSpec = item.spec;
        this.dialogMedRadio = this.medRadio;
        this.dialogBagPerBox = item.bagperbox;
        this.dialogInventoryNm = item.inventoryNm;
        this.dialogBaseprice = item.baseprice;
        this.dialogSellprice = item.sellprice;
        this.dialogChecked = item.checked;
        this.dialogMedId = item.id;
      },

      clearVariable: function(){
        this.dialogMedName = '';
        this.dialogAlias = '';
        this.dialogSpec = '';
        this.dialogMedRadio = '草药';
        this.dialogBagPerBox = '';
        this.dialogInventoryNm = '';
        this.dialogBaseprice = '';
        this.dialogSellprice = '';
        this.dialogChecked = '';
      },

      cancelDialog: function(){
        this.clearVariable();
        this.adddialog = false;
      },

      saveDialog: function(){
        if(this.dialogTitle === "修改药品"){
          this.$http.post('/api/updateMedbyId',{   
              dbs : this.$store.state.user.dbs_prefix+'medlist',      
              medname : this.dialogMedName,
              alias : this.dialogAlias,
              spec : this.dialogSpec,
              medtype : this.dialogMedRadio,
              bagperbox : this.dialogBagPerBox,
              inventoryNm : this.dialogInventoryNm,
              baseprice : this.dialogBaseprice,
              sellprice : this.dialogSellprice,
              checked : this.dialogChecked,
              id: this.dialogMedId            
          }).then( (res) => {
            this.snackbar = true;
            this.notification = '修改成功';
            this.snackbarColor = 'green';
            this.getAll();
          })
        }else if(this.dialogTitle === "新增药品"){
          this.$http.post('/api/insertMed',{
              dbs : this.$store.state.user.dbs_prefix+'medlist',          
              medname : this.dialogMedName,
              alias : this.dialogAlias,
              spec : this.dialogSpec,
              medtype : this.dialogMedRadio,
              bagperbox : this.dialogBagPerBox,
              inventoryNm : this.dialogInventoryNm,
              baseprice : this.dialogBaseprice,
              sellprice : this.dialogSellprice,
              checked : this.dialogChecked,           
          }).then( (res) => {
            //alert(res.data.insertId);
            this.snackbar = true;
            this.notification = '增加成功';
            this.snackbarColor = 'green';
            this.getAll();
          })
        }
        this.adddialog = false;
        this.clearVariable();        
      },

      deleteItem(medId){
        this.$http.delete('/api/deleteMedbyId',{
          params: {
            dbs : this.$store.state.user.dbs_prefix+'medlist',
						id : medId
					}
        }).then( (res) => {
          this.snackbar = true;
          this.notification = '删除成功';
          this.snackbarColor = 'green';
          this.getAll();
        })
      }
    },

    mounted: function() {
			this.getAll();
		}


  }
