// Utilities
  import {
    mapMutations,
    mapState
  } from 'vuex'

  export default {
    props: {
      opened: {
        type: Boolean,
        default: false
      }
    },
    data: () => ({
      links: [
        {
          to: '/dashboard',
          icon: 'mdi-view-dashboard',
          text: '首页'
        },
        {
          to: '/med-list',
          icon: 'mdi-pill',
          text: '药品管理'
        },
        {
          to: '/ord-list',
          icon: 'mdi-clipboard-outline',
          text: '处方管理'
        },
        {
          to: '/create-ord',
          icon: 'mdi-format-font',
          text: '生成处方'
        },
        {
          to: '/patient-list',
          icon: 'mdi-human-male-female',
          text: '病人管理'
        },
        {
          to: '/detail-info',
          icon: 'mdi-receipt',
          text: '详细账单'
        }
      ]
    }),

    computed: {
      ...mapState('app', ['image', 'color']),
      inputValue: {
        get () {
          return this.$store.state.app.drawer
        },
        set (val) {
          this.setDrawer(val)
        }
      }
    },

    methods: {
      ...mapMutations('app', ['setDrawer', 'toggleDrawer'])
    }
  }
